  if (num >= 1000000) return (num / 1000000).toFixed(1) + 'M';
  if (num >= 1000) return (num / 1000).toFixed(1) + 'K';
  return num.toString();
}

// Generate slug from text
export function generateSlug(text: string): string {
  return text
    .toLowerCase()
    .replace(/[^\w\s-]/g, '')
    .replace(/\s+/g, '-')
    .replace(/--+/g, '-')
    .trim();
}

// Parse JSON safely
export function parseJSON<T>(json: string, fallback: T): T {
  try {
    return JSON.parse(json) as T;
  } catch {
    return fallback;
  }
}

// Get cheaper platform
export function getCheaperPlatform(shopeePrice: number, tiktokPrice: number): 'shopee' | 'tiktok' {
  return tiktokPrice < shopeePrice ? 'tiktok' : 'shopee';
}

// Calculate savings
export function calculateSavings(shopeePrice: number, tiktokPrice: number): number {
  return Math.abs(shopeePrice - tiktokPrice);
}
